import React, { useState, useEffect } from 'react';
import { GoogleMap, useLoadScript, Marker, InfoWindow,LoadScript } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '500px'
};

const center = {
  lat: 44.651070,  // Latitude for Halifax
  lng: -63.582687  // Longitude for Halifax
};

function LocalResources() {
  const { isLoaded } = useLoadScript({
    googleMapsApiKey: 'AIzaSyCxpNpc1eJJs8pqGC43iNzjXFsktN1zAuM',
    libraries: ['places']
  });

  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);

  useEffect(() => {
    if (isLoaded) {
      const service = new window.google.maps.places.PlacesService(document.createElement('div'));
      const request = {
        location: center,
        radius: '5000', // Radius in meters
        keyword: 'mental health clinic'
      };

      service.nearbySearch(request, (results, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
          setMarkers(results.map(place => ({
            position: place.geometry.location,
            name: place.name,
            address: place.vicinity
          })));
        }
      });
    }
  }, [isLoaded]);

  return (
    <LoadScript
      googleMapsApiKey="AIzaSyCxpNpc1eJJs8pqGC43iNzjXFsktN1zAuM"
      libraries={['places']}
    >
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={12}
      >
        {markers.map((marker, index) => (
          <Marker
            key={index}
            position={marker.position}
            onClick={() => setSelectedMarker(marker)}
          />
        ))}

        {selectedMarker && (
          <InfoWindow
            position={selectedMarker.position}
            onCloseClick={() => setSelectedMarker(null)}
          >
            <div>
              <h4>{selectedMarker.name}</h4>
              <p>{selectedMarker.address}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScript>
  );
}

export default LocalResources;
