import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './OnlineResources.css';

function OnlineResources() {
  const [articles, setArticles] = useState([]);
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get('http://localhost:3001/articles');
        setArticles(response.data);
      } catch (error) {
        console.error("Error fetching articles:", error);
      }
    };

    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:3001/videos');
        setVideos(response.data);
      } catch (error) {
        console.error("Error fetching videos:", error);
      }
    };

    fetchArticles();
    fetchVideos();
  }, []);

  return (
    <div className="online-resources">
      <h2>Online Resources</h2>
      <h2>Articles</h2>
      <div className="articles">
        {articles.map(article => (
          <div key={article.id} className="resource" style={{ backgroundColor: article.color }}>
            <div className="image-placeholder">
            <img src={require('../assets/img/depression.jpg')} alt="disease" />
              </div>
            <h3>{article.title}</h3>
            <p class="content">{article.content}</p>
          </div>
        ))}
      </div>
      <h2>Videos</h2>
      <div className="videos">
        {videos.map(video => (
          <div key={video.id} className="resource" style={{ backgroundColor: video.color }}>
            <div className="video-placeholder">
              <div className="play-button">&#9658;</div>
            </div>
            <h3>{video.title}</h3>
            <p class="content">{video.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default OnlineResources;

