import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../styles/MeetingScheduler.css';

const mockPatients = ["Jack Bauer", "Jessica Lannister", "Jon Snow"];
const mockTherapists = ["Dr. House", "Dr. Strange", "Dr. Watson"];

function MeetingScheduler() {
  const location = useLocation();
  const userType = location.state?.userType || "Therapist";
  const isTherapist = userType === "Therapist";

  const [showPrompt, setShowPrompt] = useState(false);
  const [selectedUser, setSelectedUser] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedType, setSelectedType] = useState('');
  const [meetings, setMeetings] = useState([]);
  const [rescheduleMeeting, setRescheduleMeeting] = useState(null);

  const handleScheduleClick = () => {
    setShowPrompt(true);
    setRescheduleMeeting(null);
  };

  const handleClosePrompt = () => {
    setShowPrompt(false);
    setSelectedUser('');
    setSelectedDate(new Date());
    setSelectedType('');
    setRescheduleMeeting(null);
  };

  const handleUserChange = (event) => {
    setSelectedUser(event.target.value);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleTypeChange = (event) => {
    setSelectedType(event.target.value);
  };

  const handleScheduleMeeting = () => {
    const formattedDate = selectedDate.toISOString().split('T')[0];
    const newMeeting = {
      date: formattedDate,
      user: selectedUser,
      type: selectedType
    };

    setMeetings([...meetings, newMeeting]);
    handleClosePrompt();
    alert('Meeting successfully booked!');
  };

  const handleRescheduleMeeting = () => {
    const formattedDate = selectedDate.toISOString().split('T')[0];

    setMeetings(meetings.map(meeting =>
      meeting === rescheduleMeeting ? { ...meeting, date: formattedDate } : meeting
    ));
    handleClosePrompt();
    alert('Meeting successfully rescheduled!');
  };

  const handleReschedule = (meeting) => {
    setSelectedUser(meeting.user);
    setSelectedDate(new Date(meeting.date));
    setSelectedType(meeting.type);
    setRescheduleMeeting(meeting);
    setShowPrompt(true);
  };

  const handleCancel = (meeting) => {
    setMeetings(meetings.filter(m => m !== meeting));
  };

  const getMeetingsForSelectedDate = (date) => {
    const formattedDate = date.toISOString().split('T')[0];
    return meetings.filter(meeting => meeting.date === formattedDate);
  };

  return (
    <div className="main-cont">
      <div className="left-section">
        <div className="meeting-scheduler">
          <div className="flex-con">
            <h2>Meeting Scheduler</h2>
            <p>Schedule and manage your meetings</p>
          </div>
        </div>
        <div className="calendar">
          <Calendar
            onChange={handleDateChange}
            value={selectedDate}
            className="custom-calendar"
          />
        </div>
      </div>
      <div className="upcoming">
        <div className="upcomingMeetingsGrid">
          <div className='headerTextUpcoming'>
            <h2 className='headerUpcoming'>Meetings on {selectedDate.toISOString().split('T')[0]}</h2>
          </div>
          {getMeetingsForSelectedDate(selectedDate).map((meeting, index) => (
            <div className='meeting' key={index}>
              <h3>{meeting.date}</h3>
              <p>{meeting.user}</p>
              <p>{meeting.type}</p>
              <button onClick={() => handleReschedule(meeting)}>Reschedule</button>
              <button onClick={() => handleCancel(meeting)}>Cancel</button>
            </div>
          ))}
          <button className='scheduleButton' onClick={handleScheduleClick}>
            Schedule a Meeting
          </button>
        </div>
      </div>
      {showPrompt && (
        <div className='prompt'>
          <h2>Meeting Type</h2>
          <div>
            <input type="radio" id="oneOnOne" name="meetingType" value="One-on-One Meeting" onChange={handleTypeChange} checked={selectedType === 'One-on-One Meeting'} />
            <label htmlFor="oneOnOne">One-on-One</label>
            <input type="radio" id="impromptu" name="meetingType" value="Impromptu Meeting" onChange={handleTypeChange} checked={selectedType === 'Impromptu Meeting'} />
            <label htmlFor="impromptu">Impromptu</label>
          </div>
          <select onChange={handleUserChange} value={selectedUser}>
            <option value="">Select {isTherapist ? 'Patient' : 'Therapist'}</option>
            {(isTherapist ? mockPatients : mockTherapists).map((user, index) => (
              <option key={index} value={user}>{user}</option>
            ))}
          </select>
          {rescheduleMeeting ? (
            <button onClick={handleRescheduleMeeting}>Reschedule Meeting</button>
          ) : (
            <button onClick={handleScheduleMeeting}>Schedule a meeting</button>
          )}
          <button onClick={handleClosePrompt}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default MeetingScheduler;
