import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Calendar } from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import "../styles/MeetingScheduler.css";

function MeetingScheduler() {
    const location = useLocation();
    const userType = location.state?.userType || "Therapist";
    const [showPrompt, setShowPrompt] = useState(false);
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [selectedType, setSelectedType] = useState('');
    const [selectedPerson, setSelectedPerson] = useState('');
    const [meetings, setMeetings] = useState([]);

    const handleScheduleClick = () => {
        setShowPrompt(true);
    };

    const handleClosePrompt = () => {
        setShowPrompt(false);
        setSelectedDate(new Date());
        setSelectedType('');
        setSelectedPerson('');
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    const handleTypeChange = (event) => {
        setSelectedType(event.target.value);
    };

    const handlePersonChange = (event) => {
        setSelectedPerson(event.target.value);
    };

    const handleScheduleMeeting = () => {
        const newMeeting = {
            date: selectedDate,
            person: selectedPerson,
            type: selectedType
        };
        setMeetings([...meetings, newMeeting]);
        handleClosePrompt();
    };

    const handleReschedule = (index) => {
        const meeting = meetings[index];
        setSelectedDate(new Date(meeting.date));
        setSelectedType(meeting.type);
        setSelectedPerson(meeting.person);
        setShowPrompt(true);
        setMeetings(meetings.filter((_, i) => i !== index));
    };

    const handleCancel = (index) => {
        setMeetings(meetings.filter((_, i) => i !== index));
    };

    const getMeetingsForDate = (date) => {
        return meetings.filter(meeting => new Date(meeting.date).toDateString() === date.toDateString());
    };

    const tileContent = ({ date, view }) => {
        if (view === 'month' && getMeetingsForDate(date).length > 0) {
            return <div className="dot"></div>;
        }
    };

    return (
        <div className="main-cont">
            <div className="left-section">
                <div className="meeting-scheduler">
                    <div className="flex-con">
                        <h2>Meeting Scheduler</h2>
                        <p>Schedule and manage your meetings</p>
                        <button className="scheduleButton" onClick={handleScheduleClick}>Schedule a Meeting</button>
                    </div>
                </div>
                <div className="calendar">
                    <Calendar
                        className="custom-calendar"
                        onChange={handleDateChange}
                        value={selectedDate}
                        tileContent={tileContent}
                    />
                </div>
            </div>
            <div className="upcoming">
                <div className="upcomingMeetingsGrid">
                    <div className='headerTextUpcoming'>
                        <h2 className='headerUpcoming'>Upcoming meetings</h2>
                    </div>
                    {meetings.map((meeting, index) => (
                        <div className="meeting" key={index}>
                            <h3>{new Date(meeting.date).toDateString()}</h3>
                            <p>{meeting.person}</p>
                            <p>{meeting.type}</p>
                            <button onClick={() => handleReschedule(index)}>Reschedule</button>
                            <button onClick={() => handleCancel(index)}>Cancel</button>
                        </div>
                    ))}
                </div>
            </div>
            {showPrompt && (
                <div className="prompt">
                    <h2>Schedule a Meeting</h2>
                    <div>
                        <input type="radio" id="oneOnOne" name="meetingType" value="One-on-One Meeting" onChange={handleTypeChange} checked={selectedType === 'One-on-One Meeting'} />
                        <label htmlFor="oneOnOne">One-on-One</label>
                        <input type="radio" id="impromptu" name="meetingType" value="Impromptu Meeting" onChange={handleTypeChange} checked={selectedType === 'Impromptu Meeting'} />
                        <label htmlFor="impromptu">Impromptu</label>
                    </div>
                    <select onChange={handlePersonChange} value={selectedPerson}>
                        <option value="">Select {userType === "Therapist" ? "Patient" : "Therapist"}</option>
                        <option value="Person 1">Person 1</option>
                        <option value="Person 2">Person 2</option>
                        <option value="Person 3">Person 3</option>
                    </select>
                    <Calendar
                        onChange={handleDateChange}
                        value={selectedDate}
                        className="prompt-calendar"
                    />
                    <button onClick={handleScheduleMeeting}>Schedule</button>
                    <button onClick={handleClosePrompt}>Cancel</button>
                </div>
            )}
        </div>
    );
}

export default MeetingScheduler;
