import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Calendar } from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Make sure to include the calendar CSS
import "../styles/MeetingScheduler.css";

const patients = ['Jon Snow', 'Jessica Lannister'];
const therapists = ['Dr. House', 'Dr. Watson', 'Dr. Strange'];

function MeetingScheduler() {
    const location = useLocation();
    const userType = location.state?.userType || "Therapist";
    const [meetingType, setMeetingType] = useState('');
    const [selectedPerson, setSelectedPerson] = useState('');
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [upcomingMeetings, setUpcomingMeetings] = useState([]);

    const handleScheduleMeeting = () => {
        const personList = userType === 'Therapist' ? patients : therapists;
        const newMeeting = {
            date: selectedDate.toLocaleDateString(),
            person: selectedPerson,
            type: meetingType,
        };
        setUpcomingMeetings([...upcomingMeetings, newMeeting]);
    };

    return (
        <div className="main-cont">
            <div className="left-section">
                <div className="meeting-scheduler">
                    <div className="flex-con">
                        <h2>Meeting Scheduler</h2>
                        <p>Schedule and manage your meetings</p>
                    </div>
                </div>
                <div className="calendar">
                    <Calendar
                        className="custom-calendar"
                        onChange={setSelectedDate}
                        value={selectedDate}
                    />
                </div>
                <div className="meeting-prompt">
                    <button onClick={() => setMeetingType('impromptu')}>Schedule Impromptu Meeting</button>
                    <button onClick={() => setMeetingType('one-on-one')}>Schedule One-on-One Meeting</button>
                </div>
                {meetingType && (
                    <div className="person-select">
                        <h3>Select {userType === 'Therapist' ? 'Patient' : 'Therapist'}</h3>
                        <select onChange={(e) => setSelectedPerson(e.target.value)}>
                            <option value="">Select</option>
                            {(userType === 'Therapist' ? patients : therapists).map((person, index) => (
                                <option key={index} value={person}>{person}</option>
                            ))}
                        </select>
                    </div>
                )}
                {selectedPerson && (
                    <div className="schedule-confirm">
                        <button onClick={handleScheduleMeeting}>Schedule</button>
                    </div>
                )}
            </div>
            <div className="upcoming">
                <div className="upcomingMeetingsGrid">
                    <div className='headerTextUpcoming'>
                        <h2 className='headerUpcoming'>Upcoming meetings</h2>
                    </div>
                    {upcomingMeetings.map((meeting, index) => (
                        <div className="meeting" key={index}>
                            <h3>{meeting.date}</h3>
                            <p>{meeting.person}</p>
                            <p>{meeting.type}</p>
                            <button>Reschedule</button>
                            <button>Cancel</button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default MeetingScheduler;
